import React, { Component } from 'react';
import { connect } from 'react-redux';

//brining in material ui Card
import Card from '@material-ui/core/Card';
import { withStyles } from '@material-ui/core/styles';
import CardMedia from '@material-ui/core/CardMedia';

const styles = theme => ({
  card: {
    maxWidth: 400,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  actions: {
    display: 'flex',
  },

});

class CuratorsGallery extends Component {
    // Renders the entire app on the DOM

  componentDidMount() {
    this.getArtwork();
  }
  getArtwork() {
    this.props.dispatch({ type: 'FETCH_GALLERY' })
  }
    render() {
      console.log('Art lives on here', this.props.setArtworkReducer);

        return (
            <>
            <div>
              {/* <p>{JSON.stringify(this.props.setArtworkReducer)}</p> */}
              {this.props.setArtworkReducer.map((item) => (

                <img key={item.id} src={item.image} />


            ))}
          </div>
            </>
        );
    }
}

const mapReduxStateToProps = (reduxState) => {
  return reduxState;
}


export default withStyles(styles)(
  connect(mapReduxStateToProps)(PlantItem)
);
