import React, { Component } from 'react';
import { connect } from 'react-redux';


class addArtWorkForm extends Component {
state= {
    size: '',
    color: '',
    price: '',
    style: '',
    description: '',
    name: '',
    title: '',

    }


handleChangeFor

    render() {
        return (
            <>
                <div>
                    <form onSubmit={this.addNewArtwork}>
                        <input value={this.state.size} onChange={this.handleChangeFor('name')} placeholder="name" />
                        <input value={this.state.price} onChange={this.handleChangeFor('pric')} type="date" />
                        <select value={this.state.color} onChange={this.handleChangeFor('tag_id')}>
                            <option>- SELECT ACCENT COLOR -</option>
                            {this.props.tags.map(tag =>
                                <option key={tag.id}>{tag.name}</option>
                            )}
                        </select>
                        <input value={this.state.style} onChange={this.handleChangeFor('github')} placeholder="Github" />
                        <input value={this.state.name} onChange={this.handleChangeFor('website')} placeholder="Website (optional)" />
                        <input value={this.state.description} onChange={this.handleChangeFor('description')} placeholder="Description" />
                         <input value={this.state.title} onChange={this.handleChangeFor('description')} placeholder="Description" />
                        <input type="submit" value="Submit" />
                    </form>
                </div>
            </>
        );
    }
}

const mapReduxStateToProps = (reduxState) => {
    return reduxState;
}


export default connect(mapReduxStateToProps)(addArtWorkForm);
