import React, { Component } from 'react';
import { connect } from 'react-redux';


class addArtWorkForm extends Component {
state= {
    image:'',
    size: '',
    color: '',
    price: '',
    style: '',
    description: '',
    name: '',
    title: '',

    }


handleChangeFor = (property) => (event) => {


    this.setState({
    ...this.state, 
    [property]: event.target.value,
    })

}

addNewArtwork = () => {
  console.log(this.state);
    this.props.dispatch({
        type: "ADD_ARTWORK",
        payload: this.state,

    })
}


render() {
        return (
            <>
                <div>
                    <form onSubmit={this.addNewArtwork}>
                       <input value={this.state.image} onChange={this.handleChangeFor('image')} placeholder="url" />
                        <input value={this.state.size} onChange={this.handleChangeFor('size')} placeholder="Size of ArtWork" />
                        <input value={this.state.price} onChange={this.handleChangeFor('price')} placeholder type="Price" />
                         <input value={this.state.color} onChange={this.handleChangeFor('color')} type="Color" />
                        <input value={this.state.style} onChange={this.handleChangeFor('style')} placeholder="Style" />
                        <input value={this.state.name} onChange={this.handleChangeFor('name')} placeholder="Name" />
                        <input value={this.state.description} onChange={this.handleChangeFor('description')} placeholder="Description" />
                         <input value={this.state.title} onChange={this.handleChangeFor('title')} placeholder="Title Of Piece" />
                        <button> ADD ARTWORK TO GALLERY </button>
                    </form>
                </div>
            </>
        );
    }
}

const mapReduxStateToProps = (reduxState) => {
    return reduxState;
}


export default connect(mapReduxStateToProps)(addArtWorkForm);
