import React, { Component } from 'react';
import { connect } from 'react-redux';

//brining in material ui Card
import Card from '@material-ui/core/Card';
import { withStyles } from '@material-ui/core/styles';
import CardMedia from '@material-ui/core/CardMedia';

const styles = theme => ({
  card: {
    maxWidth: 400,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  actions: {
    display: 'flex',
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
});



class CuratorsGallery extends Component {
  // Renders the entire app on the DOM

  componentDidMount() {
    this.getArtwork();
  }
  getArtwork() {
    this.props.dispatch({ type: 'FETCH_GALLERY' })
  }
  render() {
    console.log('TJ', this.props.setArtworkReducer);

    return (
      <card className={classes.card}>





        {/* <p>{JSON.stringify(this.props.setArtworkReducer)}</p> */}
        {this.props.setArtworkReducer.map((item) => (  <img key={item.id} src={item.image} />   ))}



      </card>








    );
  }
}

const mapReduxStateToProps = (reduxState) => {
  return reduxState;
}


export default withStyles(styles)(
  connect(mapReduxStateToProps)(CuratorsGallery)
);
