//src/components/NewPlantItem/NewPlantItem.js

import React, { Component } from 'react';
import { connect } from 'react-redux';
import Grid from '@material-ui/core/Grid';

// Material UI
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button';
import { CardContent, CardActions } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import Typo from '@material-ui/core/Typography';

const styles = {
  card: {
    minWidth: 275,
    maxWidth: 350,
    margin: 22,
    backgroundColor: '#947388',

  },
  delete: {
    width: '100%',
    backgroundColor: 'white',
  },
  cardText: {
    color: 'white',
  }
};


class CuratorsGallery extends Component {
  // Renders the entire app on the DOM

  componentDidMount() {
    this.getArtwork();
  }
  getArtwork() {
    this.props.dispatch({ type: 'FETCH_GALLERY' })
  }
  render() {
    console.log('art lives on', this.props.setArtworkReducer);

    return (

      <Grid item xs={3}>
        <Card className={this.props.classes.card}>

          <CardContent>

            <Typo>

              {this.props.setArtworkReducer.map((item) => (<img key={item.id} src={item.image} />))}

            </Typo>

          </CardContent>

          <CardActions>
            <Button>View Work</Button>
            <Button>Art Status</Button>
          </CardActions>

        </Card>
      </Grid>



    );
  }
}

const mapReduxStateToProps = (reduxState) => {
  return reduxState;
}


export default withStyles(styles)(
  connect(mapReduxStateToProps)(CuratorsGallery)
);
