import React, { Component } from 'react';
import { connect } from 'react-redux';
import GalleryComponent from '../GalleryComponent/GalleryComponent';


class CuratorsGallery extends Component {

  componentDidMount() {
    this.getArtwork();
  }

  //GET entire gallery of work
  getArtwork() {
    this.props.dispatch({ type: 'FETCH_GALLERY' })
  }

  //displays the gallery of work 
  displayArt() {
    console.log('inside disp function', this.props.setArtworkReducer);
    return this.props.setArtworkReducer.filter(item => item.size).map(item =>
      <GalleryComponent key={item.id} item={item} />)
  }


  render() {
    return (
      <>
     <div>
     <select onChange={this.handleChange}>
        <option value=''>STYLE</option>
        <option value="1">Modern Art Work</option>
        <option value="2">Digital Prints</option>
        <option value="3">Pop Art</option>
        <option value="4">Abstract Landscape</option>
        <option value="1">Realist Landscape</option>
        <option value="2">Graffiti</option>
        <option value="3">Photography</option>
        <option value="4">Painting</option>
        <option value="3">3D Artwork</option>
        <option value="4">Hyperrealism</option>
        <option value="1">Magical Realism</option>
        <option value="2">Impressionism</option>
        <option value="3">Abstract Minimalism</option>
        <option value="4">Mixed Media</option>
        <option value="4">Miscellaneous</option>
            <option value="3">Works on Paper</option>
            <option value="4">Assemblage</option>
            <option value="4">Collage</option>

      </select>
      </div >  
      <div>
        <select onChange={this.handleChange}>
          <option value=''>COLOR</option>
          <option value="1">Pastels</option>
          <option value="2">Black and White</option>
          <option value="3">Neon Electric</option>
          <option value="4">Warm</option>
            <option value="1">Cool Tones</option>
            <option value="2">Monochromatic</option>
            <option value="3">Retro Cool</option>
            <option value="4">Primary Colors</option>
        </select>
      </div >  
      <div>
        <select onChange={this.handleChange}>
          <option value=''>Select Age Group Here</option>
          <option value="1" className="infant">Infant</option>
          <option value="2" className="toddler">Toddler</option>
          <option value="3" className="schoolAge">School Age</option>
          <option value="4" className="teens">Teens</option>
        </select>
      </div >  
          <div>
            {this.displayArt()}
          </div>

      </>
    );
  }
}

const mapReduxStateToProps = (reduxState) => {
  return reduxState;
}


export default connect(mapReduxStateToProps)(CuratorsGallery);
